import { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../../Dashboard/AdminDash/AdminDash.css";

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [unusedApiKeys, setUnusedApiKeys] = useState([]);
  const [apiKeyOwners, setApiKeyOwners] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const navigate = useNavigate();
  const adminUsername = localStorage.getItem("username") || "Admin";

  useEffect(() => {
    fetchUsers();
    fetchUnusedApiKeys();
  }, []);

  const fetchUsers = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get("http://localhost:3000/admin/users", {
        headers: { Authorization: `Bearer ${token}` },
      });

      const filteredUsers = response.data.filter(user => user.username !== adminUsername);
      setUsers(filteredUsers);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  const fetchUnusedApiKeys = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get("http://localhost:3000/admin/unused-api-keys", {
        headers: { Authorization: `Bearer ${token}` },
      });

      setUnusedApiKeys(response.data);
      if (response.data.length > 0) {
        fetchApiKeyOwners(response.data);
      }
    } catch (error) {
      console.error("Error fetching unused API keys:", error);
    }
  };

  const fetchApiKeyOwners = async (unusedKeys) => {
    if (unusedKeys.length === 0) return;
    try {
      const token = localStorage.getItem("token");
      const response = await axios.post("http://localhost:3000/admin/api-key-owners", 
        { apiKeys: unusedKeys.map(key => key.api_key) }, 
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setApiKeyOwners(response.data);
    } catch (error) {
      console.error("Error fetching API key owners:", error);
    }
  };

  const revokeApiKey = async (apiKey) => {
    if (!window.confirm("Are you sure you want to revoke this API key?")) return;
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`http://localhost:3000/admin/revoke-api-key/${apiKey}`, {
        headers: { Authorization: `Bearer ${token}` },
      });

      alert("API Key Revoked!");
      fetchUnusedApiKeys();
    } catch (error) {
      console.error("Error revoking API key:", error);
    }
  };

  const getRandomColor = () => {
    const colors = ["#FF5733", "#33FF57", "#5733FF", "#FFC300", "#33FFF3", "#F333FF"];
    return colors[Math.floor(Math.random() * colors.length)];
  };

  return (
    <div className="admin-dashboard">
      <div className="left-panel">
        <div className="admin-profile">
          <div className="profile-circle" style={{ backgroundColor: getRandomColor() }}>
            {adminUsername.slice(0, 2).toUpperCase()}
          </div>
          <h3>{adminUsername}</h3>
        </div>

        <div className="users-table">
          <h4>All Users</h4>
          <input type="text" placeholder="Search users..." value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} />
          <div className="scrollable-table">
            <table>
              <thead>
                <tr><th>Username</th><th>Role</th></tr>
              </thead>
              <tbody>
                {users.filter(user => user.username.toLowerCase().includes(searchTerm.toLowerCase())).map((user, index) => (
                  <tr key={index}><td>{user.username}</td><td>{user.role}</td></tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>

      <div className="right-panel">
        <header>
          <h2>Admin Controls</h2>
          <button className="logout-btn" onClick={() => navigate("/login")}>Logout</button>
        </header>

        <div className="api-alerts">
          <h4>ðŸš¨ API Keys Not Used in 2 Days</h4>
          {unusedApiKeys.length > 0 ? (
            <table>
              <tbody>{unusedApiKeys.map((key, index) => (
                <tr key={index}><td>************{key.api_key.slice(-4)}</td><td>{new Date(key.last_used_date).toLocaleString()}</td>
                <td><button className="revoke-btn" onClick={() => revokeApiKey(key.api_key)}>ðŸš« Revoke</button></td></tr>))}
              </tbody>
            </table>
          ) : <p>No API Key alerts.</p>}
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
