import { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const UserDashboard = () => {
  const [apiKey, setApiKey] = useState("");
  const [maskedApiKey, setMaskedApiKey] = useState("************");
  const [showKey, setShowKey] = useState(false);
  const [country, setCountry] = useState("");
  const [countryData, setCountryData] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    fetchApiKey();
  }, []);

  const fetchApiKey = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get("http://localhost:3000/auth/get-api-key", {
        headers: { Authorization: `Bearer ${token}` },
      });
      if (response.data.apiKey) {
        setApiKey(response.data.apiKey);
        setMaskedApiKey(`************${response.data.apiKey.slice(-4)}`);
      }
    } catch (error) {
      console.error("Error fetching API key:", error);
    }
  };

  const toggleShowKey = () => setShowKey(!showKey);

  const copyToClipboard = () => {
    navigator.clipboard.writeText(apiKey);
    alert("API Key copied!");
  };

  const regenerateApiKey = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem("token");
      const response = await axios.post("http://localhost:3000/auth/generate-api-key", {}, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setApiKey(response.data.apiKey);
      setMaskedApiKey(`************${response.data.apiKey.slice(-4)}`);
      alert("New API Key generated!");
    } catch (error) {
      alert("Error generating API key");
    }
    setLoading(false);
  };

  const fetchCountryData = async () => {
    if (!country) return alert("Enter a country name!");
    setLoading(true);
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get(`http://localhost:3000/countries/${country}`, {
        headers: { 
          Authorization: `Bearer ${token}`, 
          "x-api-key": apiKey 
        },
      });
      setCountryData(response.data);
    } catch (error) {
      alert("Country not found or API issue.");
    }
    setLoading(false);
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  return (
    <div className="user-dashboard">
      <header>
        <h1>🌍 User Dashboard</h1>
        <button className="logout-btn" onClick={handleLogout}>Logout</button>
      </header>

      {/* API Key Section */}
      <div className="api-key-section">
        <h3>🔑 Your API Key</h3>
        <div className="api-key-box">
          <input type="text" value={showKey ? apiKey : maskedApiKey} readOnly />
          <button onClick={toggleShowKey}>{showKey ? "🙈 Hide" : "👁 Show"}</button>
          <button onClick={copyToClipboard}>📋 Copy</button>
        </div>
        <button className="regenerate-btn" onClick={regenerateApiKey} disabled={loading}>
          {loading ? "Generating..." : "🔄 Regenerate API Key"}
        </button>
      </div>

      {/* Country Search Section */}
      <div className="country-search">
        <h3>🌎 Search Country Data</h3>
        <input 
          type="text" 
          placeholder="Enter country name" 
          value={country} 
          onChange={(e) => setCountry(e.target.value)} 
        />
        <button onClick={fetchCountryData} disabled={loading}>
          {loading ? "Searching..." : "🔍 Fetch Country Info"}
        </button>
      </div>

      {/* Display Country Data */}
      {countryData && (
        <div className="country-info">
          <h3>{countryData.name} 🇨🇺</h3>
          <p><strong>Capital:</strong> {countryData.capital}</p>
          <p><strong>Currency:</strong> {countryData.currency}</p>
          <p><strong>Languages:</strong> {countryData.languages.join(", ")}</p>
          <img src={countryData.flag} alt="Flag" />
        </div>
      )}
    </div>
  );
};

export default UserDashboard;
