import { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../UserDash/UserDash.css";

const UserDashboard = () => {
  const [apiKeys, setApiKeys] = useState([]);
  const [selectedApiKey, setSelectedApiKey] = useState("");
  const [showKey, setShowKey] = useState(false);
  const [country, setCountry] = useState("");
  const [countryData, setCountryData] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const username = localStorage.getItem("username") || "User";

  useEffect(() => {
    fetchApiKeys();
  }, []);

  // Fetch user's available API keys
  const fetchApiKeys = async () => {
    try {
        const token = localStorage.getItem("token");
        const response = await axios.get("http://localhost:3000/auth/get-api-keys", {
            headers: { Authorization: `Bearer ${token}` },
        });

        if (response.data.apiKeys && response.data.apiKeys.length > 0) {
            setApiKeys(response.data.apiKeys);
        } else {
            setApiKeys([]); // Ensure it's always an array
        }
    } catch (error) {
        console.error("Error fetching API keys:", error);
        setApiKeys([]); // Prevent undefined issues
    }
};


  // Generate a new API key
  const generateApiKey = async () => {
    setLoading(true);
    try {
        const token = localStorage.getItem("token");
        const response = await axios.post("http://localhost:3000/auth/generate-api-key", {}, {
            headers: { Authorization: `Bearer ${token}` },
        });

        if (response.data.apiKey) {
            setApiKeys(prevKeys => [...prevKeys, { api_key: response.data.apiKey }]); // Append new API key
        }

        alert("New API Key generated!");
    } catch (error) {
        alert("Error generating API key");
    }
    setLoading(false);
};


  // Function to Generate Random Color
  const getRandomColor = () => {
    const colors = ["#FF5733", "#33FF57", "#5733FF", "#FFC300", "#33FFF3", "#F333FF"];
    return colors[Math.floor(Math.random() * colors.length)];
  };

  // Fetch Country Data
  const fetchCountryData = async () => {
    if (!selectedApiKey) return alert("Please select an API key first!");
    if (!country) return alert("Enter a country name!");

    setLoading(true);
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get(`http://localhost:3000/countries/${country}`, {
        headers: { 
          Authorization: `Bearer ${token}`, 
          "x-api-key": selectedApiKey 
        },
      });

      setCountryData(response.data);
    } catch (error) {
      alert("Country not found or API issue.");
    }
    setLoading(false);
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  return (
    <div className="dashboard-container">
      {/* Left Panel */}
      <div className="left-panel">
        
        {/* User Profile Section */}
        <div className="user-profile">
          <div className="profile-circle" style={{ backgroundColor: getRandomColor() }}>
            {username.slice(0, 2).toUpperCase()}
          </div>
          <h3>{username}</h3>
        </div>

        {/* API Keys Table */}
        <div className="api-key-table">
          <h4>üîë Your API Keys</h4>
          {apiKeys.length > 0 ? (
            <select onChange={(e) => setSelectedApiKey(e.target.value)} value={selectedApiKey}>
              <option value="">Select an API Key</option>
              {apiKeys.map((key, index) => (
                <option key={index} value={key.api_key}>
                  {showKey && key.api_key ? key.api_key : `************${key.api_key?.slice(-4)}`}
                </option>
              ))}
            </select>
          ) : (
            <p>No API Keys Available</p>
          )}
        </div>

        {/* Generate API Key Section */}
        <div className="api-key-generate-section">
          <button className="generate-btn" onClick={generateApiKey} disabled={loading}>
            {loading ? "Generating..." : "Generate API Key"}
          </button>
        </div>
      </div>

      {/* Right Panel */}
      <div className="right-panel">
        <header>
          <h2>üåç Country Search</h2>
          <button className="logout-btn" onClick={handleLogout}>Logout</button>
        </header>

        <div className="search-section">
          <input 
            type="text" 
            placeholder="Enter country name" 
            value={country} 
            onChange={(e) => setCountry(e.target.value)} 
          />
          <button onClick={fetchCountryData} disabled={loading || !selectedApiKey}>
            {loading ? "Searching..." : "üîç Fetch Country Info"}
          </button>
        </div>

        {countryData && (
          <div className="country-card">
            <button>{countryData.name}</button>
            <button>Capital: {countryData.capital}</button>
            <button>Currency: {countryData.currency}</button>
            <button>Languages: {countryData.languages.join(", ")}</button>
            <img src={countryData.flag} alt="Flag" />
          </div>
        )}
      </div>
    </div>
  );
};

export default UserDashboard;
