import { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../../Dashboard/AdminDash/AdminDash.css"

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get("http://localhost:3000/admin/users", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(response.data);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
    setLoading(false);
  };

  const revokeApiKey = async (userId) => {
    if (!window.confirm("Are you sure you want to revoke this API key?")) return;
    setLoading(true);
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`http://localhost:3000/admin/revoke-api-key/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert("API Key Revoked!");
      fetchUsers(); // Refresh list after revocation
    } catch (error) {
      console.error("Error revoking API key:", error);
    }
    setLoading(false);
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  return (
    <div className="admin-dashboard">
      <header>
        <h1>ðŸš€ Admin Dashboard</h1>
        <button className="logout-btn" onClick={handleLogout}>Logout</button>
      </header>

      {/* Search Users */}
      <div className="search-bar">
        <input 
          type="text" 
          placeholder="Search users..." 
          value={searchTerm} 
          onChange={(e) => setSearchTerm(e.target.value)} 
        />
      </div>

      {/* User List */}
      <div className="user-list">
        {loading ? (
          <p>Loading...</p>
        ) : (
          users
            .filter(user => user.username.toLowerCase().includes(searchTerm.toLowerCase()))
            .map((user) => (
              <div key={user.id} className="user-card">
                <h3>{user.username} {user.role === "admin" && "ðŸ‘‘"}</h3>
                <p>Role: {user.role}</p>
                <button 
                  className="revoke-btn"
                  onClick={() => revokeApiKey(user.id)}
                  disabled={loading}
                >
                  ðŸš« Revoke API Key
                </button>
              </div>
            ))
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
