import { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../UserDash/UserDash.css";

const UserDashboard = () => {
  const [apiKeys, setApiKeys] = useState([]);
  const [selectedApiKey, setSelectedApiKey] = useState("");
  const [showKey, setShowKey] = useState(false);
  const [country, setCountry] = useState("");
  const [countryData, setCountryData] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    fetchApiKeys();
  }, []);

  const fetchApiKeys = async () => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get("http://localhost:3000/auth/get-api-keys", {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (response.data.apiKeys.length > 0) {
        setApiKeys(response.data.apiKeys);
        setSelectedApiKey(response.data.apiKeys[0]);
      }
    } catch (error) {
      console.error("Error fetching API keys:", error);
    }
  };

  const generateApiKey = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem("token");
      const response = await axios.post("http://localhost:3000/auth/generate-api-key", {}, {
        headers: { Authorization: `Bearer ${token}` },
      });

      setApiKeys([...apiKeys, response.data.apiKey]);
      setSelectedApiKey(response.data.apiKey);
      alert("New API Key generated!");
    } catch (error) {
      alert("Error generating API key");
    }
    setLoading(false);
  };

  const fetchCountryData = async () => {
    if (!selectedApiKey) return alert("Please select or generate an API key first!");
    if (!country) return alert("Enter a country name!");

    setLoading(true);
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get(`http://localhost:3000/countries/${country}`, {
        headers: { 
          Authorization: `Bearer ${token}`, 
          "x-api-key": selectedApiKey 
        },
      });

      setCountryData(response.data);
    } catch (error) {
      alert("Country not found or API issue.");
    }
    setLoading(false);
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  return (
    <div className="dashboard-container">
      {/* Left Panel: API Key Management */}
      <div className="left-panel">
        <h2>üîë API Keys</h2>
        {apiKeys.length > 0 ? (
          <div className="api-keys-list">
            <select onChange={(e) => setSelectedApiKey(e.target.value)} value={selectedApiKey}>
              {apiKeys.map((key, index) => (
                <option key={index} value={key}>
                  {showKey ? key : `************${key.slice(-4)}`}
                </option>
              ))}
            </select>
            <button onClick={() => setShowKey(!showKey)}>{showKey ? "üôà Hide" : "üëÅ Show"}</button>
          </div>
        ) : (
          <p>No API Keys Available</p>
        )}
        <button className="generate-btn" onClick={generateApiKey} disabled={loading}>
          {loading ? "Generating..." : "üîÑ Generate API Key"}
        </button>
      </div>

      {/* Right Panel: Country Search */}
      <div className="right-panel">
        <header>
          <h2>üåç Country Search</h2>
          <button className="logout-btn" onClick={handleLogout}>Logout</button>
        </header>

        <div className="search-section">
          <input 
            type="text" 
            placeholder="Enter country name" 
            value={country} 
            onChange={(e) => setCountry(e.target.value)} 
          />
          <button onClick={fetchCountryData} disabled={loading}>
            {loading ? "Searching..." : "üîç Fetch Country Info"}
          </button>
        </div>

        {countryData && (
          <div className="country-card">
            <h3>{countryData.name} üá®üá∫</h3>
            <p><strong>Capital:</strong> {countryData.capital}</p>
            <p><strong>Currency:</strong> {countryData.currency}</p>
            <p><strong>Languages:</strong> {countryData.languages.join(", ")}</p>
            <img src={countryData.flag} alt="Flag" />
          </div>
        )}
      </div>
    </div>
  );
};

export default UserDashboard;
